mapper.select:
SELECT
DATE(join_time) AS joinTime,
COUNT(*) AS userCount
FROM video_visitor

    AND course_id=#{courseId}


        AND DATE(join_time) >= #{startDate}



        AND DATE(join_time) <= #{endDate}


        AND DATE(join_time) = #{endDate}



GROUP BY DATE(join_time)
ORDER BY DATE(join_time)
